#include <stdio.h>
#include <stdlib.h>

int main (void)
{
 char * numString = "";
 int numArray [1000] = {0};
 int i;
 int high_index = 0;
 int high_sum = 0;
 int result = 1;

 /* Turn numString into an array for easier referencing */
 for (i = 0; i < 1000; i++)
  numArray[i] = *(numString + i) - '0';

 for(i = 0; i < 995; i+=2)
 {
  int j = 0;
  int sum = 0;
  int temp_high_index = i; 
  int left  = numArray[i];
  int right = numArray[i+13];
  
  for ( j = 0; j < 14; j++)
  {
   if (numArray[i+j] == 0)
   {
    /* 
       if a zero is encountered, skip all numbers until the zero has passed. By adding j-1 to i, we skip to the index just BEFORE the 0. However, when the loop turns over the i+=2 will skip just beyond the 0.
    */
    i += j-1; 
    continue;
   }
  }

  if (left >= right) 
  {
   for (j = 0; j < 13; j++)
    sum += numArray[i+j];
  }
  else
  {
   temp_high_index++; 

   for (j = 0; j < 13; j++)
    sum += numArray[i+j+1];
  }

  if (sum >= high_sum) 
  {
   high_sum = sum; 
   high_index = temp_high_index; 
  }
 }

 /* get product */
 for (i = high_index; i < high_index + 13; i++)
  result *= numArray[i];

 printf("Highest product is %d\n", result);
  
 return 1;
}

